mapper.update:
update sys_order
name = #{name}, phone = #{phone}, start = #{start}, end = #{end}, content = #{content}, price = #{price}, beginTime = #{beginTime}, status = #{status}, startMap = #{startMap}, endMap = #{endMap}, endTime = #{endTime}, receiveMoney = #{receiveMoney}, receiveText = #{receiveText}, costMoney = #{costMoney}, visit = #{visit}, source = #{source}, type = #{type}, is_clean = #{is_clean}, cleanText = #{cleanText}, payState = #{payState}, costText = #{costText}, cleanEmp = #{cleanEmp}, workTime = #{workTime},
where id = #{id}

mapper.select:
select

from sys_order where
order_no = #{order_no}
`name` = #{name}
phone = #{phone}
 beginTime >= #{beginTime1}   
 beginTime <= #{beginTime2}

select

from sys_order where deleteStatus = 0 and status = 2
and is_clean = #{is_clean}
and payState = #{payState}
and order_no = #{order_no}
and name like concat("%",#{name},"%")
and endTime >= #{endTime1}
and endTime <= #{endTime2}

select count(id) as f1,sum(price) as f2 from sys_order where deleteStatus = 0
and createTime >= #{startTime}
and createTime <= #{endTime}
and status = #{status}
and payState = #{payState}
and is_clean = #{is_clean}
